sys_script_include.script:
var GetLocationDetail = Class.create();
GetLocationDetail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getLocation : function(){
		var userLocation = new GlideRecord('sys_user');
		userLocation.get(this.getParameter('sysparm_locationID'));
		return userLocation.getDisplayValue('location') + '';
	},

    type: 'GetLocationDetail'
});
