sys_script_include.script:
var GetPhoneNumber = Class.create();
GetPhoneNumber.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
getPhoneNumber : function(){
	var userPhone = new GlideRecord('sys_user');
	userPhone.get(this.getParameter('sysparm_phoneID'));
	return userPhone.mobile_phone + ' ';
},
    type: 'GetPhoneNumber'
});
